var label=document.querySelector(".custum-file-upload"),text=document.querySelector(".text"),icon=document.querySelector(".icon"),picbody=document.querySelector(".picbody"),magnifier=document.querySelector(".magnifier"),submit=document.querySelector(".submitBtn"),inputbox=document.querySelectorAll("input");inputbox[0].addEventListener("change",function(n){const t=n.target.files[0];if(t){const r=t.name.split(".").pop().toLowerCase();if(!["jpg","jpeg","png","bmp","gif","heic"].includes(r)){alert("請上傳有效的圖片檔案（jpg, jpeg, png, bmp, gif, heic）");n.target.value="";return}const i=new FileReader;i.onload=function(n){const t=new Image;t.onload=function(){picbody.innerHTML='<img src="'+n.target.result+'" class="img-fluid" alt="Responsive image">';label.style.backgroundImage="url("+n.target.result+")";label.style.backgroundSize="cover";label.style.backgroundPosition="center center";text.style.display="none";icon.style.display="none";magnifier.style.display="flex";magnifier.style.justifyContent="center";magnifier.style.alignItems="center"};t.src=n.target.result};i.readAsDataURL(t)}else label.style.backgroundImage="none",text.style.display="block",icon.style.display="block",magnifier.style.display="none",picbody.innerHTML=""});const deleteButton=document.querySelector(".delete");deleteButton.addEventListener("click",function(){label.style.backgroundImage="none";text.style.display="block";icon.style.display="block";inputbox[0].value="";magnifier.style.display="none"});